import Header from './Components/HeaderNav/Header';
import Slider from 'react-animated-slider';
import './Components/HeaderNav/HeaderNav.scss';
import './index.css';





function App() {

  const slides = [
    { title: 'First item', description: 'Lorem ipsum', image: '/images/slider1.jpeg'},
    { title: 'Second item', description: 'Lorem ipsum', images: '/images/slider'}
  ];


  return (
    <div className="App">
        <Header/>
        <div className="contentSlider">
          <Slider autoplay={3000}>
            {slides.map((item, index) => (
              <div
                key={index}
                style={{ background: `url('${item.image}') no-repeat center center` }}
              >
                <div className="center">
                  <h1>{item.title}</h1>
                  <p>{item.description}</p>
                  <button>{item.button}</button>
                </div>
              </div>
            ))}
          </Slider>
        </div>
    </div>
  );
}

export default App;
