import {GET_CATEGORIES} from '../type'

const initialState = {
    categories 
    
}

export default function(state = initialState, action){

    switch(action.type){

        case GET_CATEGORIES:
        return {
            ...state,
            categories:action.payload,
            loading:false

        }
        default: return state
    }

}
