import React, { Component } from 'react'
import {connect} from 'react-redux'
import {getCategories} from '../../store/actions/categoriesActions'

class categories extends Component {
  componentDidMount(){
      this.props.getCategories()
      
  }
  render() {
      const {categories} = this.props.categories
  
      // console.log(categories)

    // for (var key in categories.categories) {
    //   var obj = categories.categories[key];
    //   var values = Object.values(obj)
    //   console.log(values[0]);
    
    // }

    const categoriesTitle = categories.categories
    

    // console.table( JSON.stringify(categories, null, 4));
      return (
        <div className="CategorieCont">
            {
              categoriesTitle.map(c=> {
                <React.Fragment>
                    <div>c.title</div>
                </React.Fragment>
              })
            }
        </div>
    )
  }
}

const mapStateToProps  = (state) => ({categories:state.categories})

export default connect(mapStateToProps, {getCategories})(categories)
