import Header from './Components/HeaderNav/Header';
import Slider from 'react-animated-slider';
import 'react-animated-slider/build/horizontal.css';
import './Components/HeaderNav/HeaderNav.scss';
import './index.css';





function App() {

  const sliderContent = [
    {
      image: '/images/slider1.jpeg',
    },
    {
      image: '/images/slider2.jpeg',
    },
    {
      image: '/images/slider1.jpeg',
    },
    {
      image: '/images/slider2.jpeg',
    },
  ];


  const sliderContentInner = [
    {
      title: 'Vulputate Mollis Ultricies Fermentum Parturient',
      description:
      'Aenean eu leo quam. Pellentesque ornare sem lacinia quam venenatis vestibulum. Fusce dapibus, tellus ac cursus commodo, tortor mauris condimentum nibh, ut fermentum massa justo sit amet risus. Cras justo odio, dapibus ac facilisis.',
      button: 'Read More',
      image: '/images/slider1.jpeg',
    },
    {
      title: 'THE IRISHMAN',
      description:
      'A',
      button: 'Buy now',
      image: '/images/slider2.jpeg',
    },
    {
      title: 'Vulputate Mollis Ultricies Fermentum Parturient',
      description:
      'Aenean eu leo quam. Pellentesque ornare sem lacinia quam venenatis vestibulum. Fusce dapibus, tellus ac cursus commodo, tortor mauris condimentum nibh, ut fermentum massa justo sit amet risus. Cras justo odio, dapibus ac facilisis.',
      button: 'Read More',
      image: '/images/slider1.jpeg',
    },
    {
      title: 'Phasellus volutpat metus',
      description:
      'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Cras mattis consectetur purus sit amet fermentum. Cras justo odio, dapibus ac facilisis in, egestas eget quam. Duis mollis, est non commodo luctus, nisi erat porttitor ligula.',
      button: 'Buy now',
      image: '/images/slider2.jpeg',
    },
  ];
  return (
    <div className="App">
        <div className="contentSlider">
          <Slider className="slider-wrapper backSlider" autoplay={4000} infinite={true}>
              {sliderContent.map((item, index) => (
            <div
              key={index}
              className="slider-content"
              style={{ background: `url('${item.image}') no-repeat center top`}}
            >
              </div>
            ))}

          </Slider>
          <Slider className="slider-wrapper innerSlider" autoplay={4000} infinite={true}>
            {sliderContentInner.map((item, index) => (
              <div
                key={index}
                className="slider-content"
                style={{ background: `url('${item.image}') no-repeat center top`}}
              >
                 <div className="inner">
                    <h1>{item.title}</h1>
                    <p>{item.description}</p>
                    <button>{item.button}</button>
                  </div>
                </div>
              ))}
          </Slider>
        </div> 
        <Header/>

    </div>
  );
}

export default App;
