import {GET_CATEGORIES, CATEGORIES_ERROR} from '../type'
import axios from 'axios'

export const getCategories = () => async dispatch => {
    
    try{
        const res = await axios.get(`http://git.bigbek.com/-/snippets/2/raw/master/tv_show_list.json`)
        dispatch( {
            type: GET_CATEGORIES,
            payload: res.data
        })
        console.log(res)
    }
    catch(e){
        dispatch( {
            type: CATEGORIES_ERROR,
            payload: console.log(e),
        })
    }

}
